import React, { useContext, useState, useEffect } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';
import { AuthContext } from '../helpers/AuthContext'

const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();

    // const [isLoggedIn, setIsLoggedIn] = useState(false);
    // const [token, setToken] = useState("")
    // let url = "http://localhost:3002"

    const { token, setToken, isLoggedIn, setIsLoggedIn } = useContext(AuthContext);

    const url = "http://localhost:3002"

    const login = () => {
        const data = { username: username, password: password }
        Axios.post(url + "/login", data).then((response) => {
            if (response.data.error) {
                // alert(response.data)
                // if there is any error, don't create an accessToken to localstorage, else if any error doesn't exist, create accessToken
                console.log("response if condition error: ", response.data.error);
                setIsLoggedIn(isLoggedIn)
                console
            }
            else {
                console.log("token: ", response.data);
                localStorage.setItem("accessToken", response.data.token)  //this comes from routes -> const accessToken -> res.json({token, username, id})
                setIsLoggedIn(!isLoggedIn);
                redirect();
            }
        })
    }



    const redirect = () => {
        history.push("/home")
    }

    return (
        <div className="loginContainer">
            <h1>Login</h1>
            <label>Username</label>
            <input type="text" onChange={(event) => { setUsername(event.target.value) }} />

            <label>Password</label>
            <input type="password" onChange={(event) => { setPassword(event.target.value) }} />

            <button onClick={login}>Login</button>
        </div>
    )
}

export default Login
