import React, { useState } from 'react'
import Edit from '../../Buttons/Edit'
import Delete from '../../Buttons/Delete'
import Popup from '../../../helpers/Popup';
import Axios from 'axios'

const Note = ({ note, deleteNote }) => {
    const [title, setTitle] = useState(note.title)
    const [description, setDescription] = useState(note.description);
    const [bleona, setBleona] = useState([note])


    const [updateNote, setUpdateNote] = useState([])
    const [isOpen, setIsOpen] = useState(false);
    const url = "http://localhost:3002/notes"
    const token = localStorage.getItem("token");

    const editNote = () => {
        setIsOpen(!isOpen);
    }
    // console.log(isOpen);

    const handleChange = (e) => {
        setUpdateNote({
            ...updateNote,
            title: e.target.value,
            description: e.target.value
        });
        // console.log(updateNote)
    };

    const updateData = (e, id) => {
        e.preventDefault();
        Axios.put((url + `/${id}`), updateNote, {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            // console.log(response.data);
            setIsOpen(!isOpen)
            // setTitle(response.data.title)
            // console.log("update ", updateNote)
            setBleona([...bleona, { title: e.target.name, description: e.target.name }])
            console.log(bleona, "BLEONA");
            // setUpdateNote([...updateNote, { title: response.data.title, description: response.data.description }]);
            // ([...propsNotes, { title: response.data.title, description: response.data.description }])

        })
            .catch((err) => {
                console.log(err)
            })

    }


    return (
        <div className="note">
            {/* <span>{date}</span> */}
            <h4>{title}</h4>
            <p>{description}</p>
            {/* <div>{note.createdAt}</div> */}
            <div className="dashboard-buttons">
                <Edit editNote={editNote} />
                <Delete deleteNote={() => deleteNote(note.id)} />
            </div>
            {
                isOpen && <Popup handleClose={editNote} handleChange={handleChange} updateData={updateData} notes={note} isOpen={isOpen} />
            }
        </div>
    )
}

export default Note
