/* eslint-disable no-unused-vars */
import React, { useState } from 'react';
import './App.scss';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom'
import Login from './components/Login'
import SignUp from './components/SignUp';
import Dashboard from './components/dashboard/Dashboard'
import ProtectedRoutes from './protectedRoutes/ProtectedRoutes';
import { ToastContainer } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'
import { AuthContext } from './helpers/AuthContext'

function App() {

  const [userId, setUserId] = useState(0)

  const login = () => {
    const data = { email: email, password: password }
    Axios.post(url + "/login", data).then((response) => {
      if (response.data.error) {
        toast.warn(response.data.error)
        // alert(response.data)
        // if there is any error, don't create an accessToken to localstorage, else if any error doesn't exist, create accessToken
        console.log("response if condition error: ", response.data.error);
      }
      else {
        toast.success(response.data.message)
        console.log("userData: ", response.data.id);
        // setUserId(response.data.id)
        localStorage.setItem("token", response.data.token); //this comes from routes -> const accessToken -> res.json({token, username, id})
        // const userData = {
        //     id: response.data.id,
        //     // name: response.data.name,
        //     // email: response.data.email
        // }
        // localStorage.setItem("user", JSON.stringify(userData));
        redirect();
      }
    })
  }

  return (
    <div>
      <AuthContext.Provider value={{ userId, setUserId }}>
        <Router>
          <Switch>
            <Route path="/login" exact component={Login} />
            <Route path="/create-account" component={SignUp} />
            <ProtectedRoutes path="/dashboard" component={Dashboard} />
            {/* <ProtectedRoutes path="/profile" exact component={Profile} />
          <ProtectedRoutes path="/add-note" exact component={AddNote} />
          <ProtectedRoutes path="/my-notes" exact component={MyNotes} />
          <ProtectedRoutes path="/notes" exact component={Notes} /> */}
            <Route exact path="/">
              <Redirect exact from="/" to="login" />
            </Route>
            <Route exact path="**" component={() => "404 NOT FOUND"} />
          </Switch>
          <ToastContainer autoClose={3000} />
        </Router>
      </AuthContext.Provider>
    </div>
  );
}

export default App;
