import React, { useState } from 'react'
import Axios from 'axios';

const CreateNote = () => {

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState();

    const url = "http://localhost:3002/notes";

    const addNote = (e) => {
        console.log(e)
        const data = { title: title, description: description }
        e.preventDefault();

        Axios.post(url + "/add-note", data).then((response) => {
            console.log(response)
        })
    }

    return (
        <div className="container-bcg d-flex">
            <form onSubmit={addNote} className="form login-form d-flex flex-column justify-content-center m-auto">
                <h1>Add note</h1>
                <div className="user-input d-flex flex-column">
                    <label>Title</label>
                    <input type="text" onChange={(event) => { setTitle(event.target.value) }} />
                </div>
                <div className="user-input d-flex flex-column">
                    <label>Description</label>
                    <input type="text" onChange={(event) => { setDescription(event.target.value) }} />
                </div>

                <div className="button-span d-flex justify-content-between align-items-center">
                    <button type="submit">Save note</button>
                    {/* <span>Create an account <Link to="create-account">here</Link></span> */}
                </div>
            </form>
        </div>
    )
}

export default CreateNote
