import React, { useState, useContext } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';
import { Link } from 'react-router-dom';
import { BiLogInCircle } from "react-icons/bi";
import { toast } from "react-toastify"
import { AuthContext } from '../helpers/AuthContext'

const Login = ({ handleChange }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const { setAuthUserId } = useContext(AuthContext)
    const history = useHistory();


    const url = "http://localhost:3002/auth"


    const login = () => {
        const data = { email: email, password: password }
        Axios.post(url + "/login", data).then((response) => {
            if (response.data.error) {
                toast.warn(response.data.error)
                // alert(response.data)
                // if there is any error, don't create an accessToken to localstorage, else if any error doesn't exist, create accessToken
                console.log("response if condition error: ", response.data.error);
            }
            else {
                toast.success(response.data.message)
                console.log("userData: ", response.data.token);
                const userData = {
                    id: response.data.userId,
                    name: response.data.name,
                    email: response.data.email
                }
                setAuthUserId({ id: response.data.userId })

                localStorage.setItem("token", response.data.token); //this comes from routes -> const accessToken -> res.json({token, username, id})
                localStorage.setItem("user", JSON.stringify(userData));
                redirect();
            }
            console.log("id", userId)
        })
    }

    const redirect = () => {
        history.push("/dashboard")
    }

    return (

        <div className="container-bcg">
            <div className="form login-form d-flex flex-column justify-content-center">
                <div className="icon text-center">
                    <BiLogInCircle />
                </div>
                <h2 className="dark-blue">Login</h2>
                <div className="form-space">
                    <input type="text" placeholder="Email" onChange={(event) => { setEmail(event.target.value) }} />
                    <input type="password" placeholder="Password" onChange={(event) => { setPassword(event.target.value) }} />

                </div>
                <div className="button-span text-center">
                    <div>
                        <button onClick={login}>Login</button>
                    </div>
                    <span>Create an account <Link to="/create-account">here</Link></span>
                </div>
            </div>
        </div>
    )
}

export default Login
