/* eslint-disable no-unused-expressions */
/* eslint-disable no-sequences */
import React, { useState } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';
import { Link } from 'react-router-dom';
import { BiLogInCircle } from "react-icons/bi";

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();


    const url = "http://localhost:3002/auth"


    const login = () => {
        const data = { email: email, password: password }
        Axios.post(url + "/login", data).then((response) => {
            if (response.data.error) {
                // alert(response.data)
                // if there is any error, don't create an accessToken to localstorage, else if any error doesn't exist, create accessToken
                console.log("response if condition error: ", response.data.error);
            }
            else {
                console.log("token: ", response.data.token);
                localStorage.setItem("token", response.data.token); //this comes from routes -> const accessToken -> res.json({token, username, id})
                localStorage.setItem("user", response.data.email);
                redirect();
            }
        })
    }

    const redirect = () => {
        history.push("/dashboard")
    }

    return (

        <div className="container-bcg">
            <div className="form d-flex flex-column justify-content-center">
                <div className="icon text-center">
                    <BiLogInCircle />
                </div>
                <h2 className="dark-blue">Sign up</h2>
                <div className="form-space">
                    <input type="text" placeholder="Email" onChange={(event) => { setEmail(event.target.value) }} />
                    <input type="password" placeholder="Password" onChange={(event) => { setPassword(event.target.value) }} />

                </div>
                <div className="button-span text-center">
                    <div>
                        <button onClick={login}>Login</button>
                    </div>
                    <span>Cre <Link to="/login">Log in</Link></span>
                </div>
            </div>
        </div>
    )
}

export default Login
