    const url = "http://localhost:3002";
    const token = localStorage.getItem("token");
    const getNotes = () => {
        Axios.get((url + "/notes"), {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            // console.log("allNotes", response.data)
