            setUser(response.data)
            console.log("this", response.data)
        })
    }


    const handleChange = (e) => {
        console.log(user)
    }

    const editProfile = (e) => {
        e.preventDefault();
        console.log("edited")
    }

    return (
        <div>
            <h1>Bleona</h1>
            <form onSubmit={(e) => editProfile(e)}>
                <input type="text" name="name" defaultValue={user.name} onChange={(e) => handleChange(e)} />
