/* eslint-disable no-unused-expressions */
/* eslint-disable no-sequences */
import React, { useContext, useState, useEffect } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';
import { AuthContext } from '../helpers/AuthContext'


const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [ls_token, setLs_token] = useState(localStorage.getItem("token"))

    // const [isLoggedIn, setIsLoggedIn] = useState(false);
    // const [token, setToken] = useState("")
    // let url = "http://localhost:3002"

    const { token, setToken } = useContext(AuthContext);

    const url = "http://localhost:3002"


    const login = () => {
        const data = { username: username, password: password }
        Axios.post(url + "/login", data).then((response) => {
            if (response.data.error) {
                // alert(response.data)
                // if there is any error, don't create an accessToken to localstorage, else if any error doesn't exist, create accessToken
                console.log("response if condition error: ", response.data.error);
                setIsLoggedIn(false)
                console.log("isss logedd out ", isLoggedIn)
                setToken("")
            }
            else {
                console.log("token: ", response.data.token);
                localStorage.setItem("token", response.data.token), //this comes from routes -> const accessToken -> res.json({token, username, id})
                    setToken({ token: response.data.token });
                setIsLoggedIn(true);
                console.log("isss logedd in ", isLoggedIn)
                redirect();
            }
        })
    }



    // const ls_token = 
    // console.log(ls_token)

    // me shtu headers, tokenin prej db
    useEffect(() => {
        Axios.get(url, {
            headers: {
                "token": ls_token
            }
        })
            .then((response) => {
                if (response.config.headers.token) {
                    setToken(localStorage.setItem("tuken", response.data))
                }
                else {
                    console.log("error te responsi   ", response.data.error)
                    setIsLoggedIn(false)
                    console.log("is logged in  ", isLoggedIn);
                    history.push("/")
                }
            })
            .catch((err) => {
                console.log("error login.js    ", err)
            })
    }, [])



    const redirect = () => {
        history.push("/home")
    }

    return (
        <div className="loginContainer">
            <h1>Login</h1>
            <label>Username</label>
            <input type="text" onChange={(event) => { setUsername(event.target.value) }} />

            <label>Password</label>
            <input type="password" onChange={(event) => { setPassword(event.target.value) }} />

            <button onClick={login}>Login</button>
        </div>
    )
}

export default Login
