import React, { useState } from 'react'
// import Axios from 'axios'
import Edit from '../../Buttons/Edit'
import Delete from '../../Buttons/Delete'
import Popup from '../../../helpers/Popup';

const MyNotes = ({ notes, deleteNote, date }) => {

    const [isOpen, setIsOpen] = useState(false);

    const editNote = (id) => {
        console.log(id);
        setIsOpen(!isOpen);
    }
    console.log(isOpen)

    return (
        <div className="switched-components">
            <div className="component-name">
                <div>{notes.length}</div><span className="dark-blue">Your notes</span>
            </div>
            <hr />

            {/* <h4>{notes.length}</h4> */}
            <div className="dashboard-container">
                {
                    notes.map((note) => {
                        return <><div className="note" key={note.id}>
                            <span>{date}</span>
                            <h4>{note.title}</h4>
                            <p>{note.description}</p>
                            {/* <div>{note.createdAt}</div> */}
                            <div className="dashboard-buttons">
                                <Edit editNote={() => editNote(note.id)} />
                                <Delete deleteNote={() => deleteNote(note.id)} />
                            </div>
                        </div>
                            {isOpen && <Popup handleClose={editNote} notes={note} />}
                        </>

                    })
                }
                {/* <div>
                    {

                    }
                </div> */}
            </div>
        </div >
    )
}

export default MyNotes
