import React, { useState } from 'react'

const Popup = ({ notes, handleClose, handleChange, updateData }) => {
    const [show, setShow] = useState(false);

    const toggle = () => setShow(!show)
    // const url = "http://localhost:3002/notes"
    // const token = localStorage.getItem("token");

    // const [note, setNote] = useState([])

    // const handleChange = (e) => {
    //     setNote({
    //         ...note,
    //         [e.target.name]: e.target.value,
    //     });
    // };

    // const updateData = (id) => {
    //     Axios.put((url + `/${id}`), note, {
    //         headers: {
    //             "token": `${token}`
    //         }
    //     }).then((response) => {
    //         console.log(response.data);

    //     })
    //         .catch((err) => {
    //             console.log(err)
    //         })

    // }

    // console.log("popup", notes)
    return (

        <div id="outer-container" className={show ? 'modal modal-content-active' : 'close'}>
            {
                show ?
                    <div className='modal-box'>
                        <button onClick={toggle}>X</button>
                        <div>
                            <form onSubmit={() => updateData(notes.id)}>
                                <span className="close-icon" onClick={handleClose}>X</span>
                                <input type="text" name="title" defaultValue={notes.title} onChange={(e) => handleChange(e)} />
                                <input type="text" name="description" defaultValue={notes.description} onChange={(e) => handleChange(e)} />
                                <button type="submit">Save data</button>
                            </form>
                        </div>
                    </div>
                    :
                    null
            }
        </div>

    )
}

export default Popup
