import React from 'react'
import { Link } from 'react-router-dom'
import { useState } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';
import { BsFillCalendar3WeekFill } from "react-icons/bs";

const SignUp = () => {
    const [name, setName] = useState("");
    const [surname, setSurname] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const url = "http://localhost:3002/auth";
    let history = useHistory()
    const signUp = () => {

        const data = { name, surname, email, password, confirmPassword }

        Axios.post(url + "/signup", data)
            .then((response) => {
                if (response.data.error) {
                    console.log(response.data)
                }
                else {
                    console.log(response.data, "user has been successfully created")
                    // console.log(response)
                    history.push("/login")
                }

            })

    }

    return (
        <div className="container-bcg d-flex">
            <div className="form d-flex flex-column justify-content-center m-auto">
                <div>
                    <BsFillCalendar3WeekFill />
                </div>
                <h1>Sign up</h1>
                
            </div>
        </div>
    )
}

export default SignUp
