    // }, [])

    return (
        <div>
            <h1>Edit profile</h1>
            <form onSubmit={(e) => editProfile(e)}>
                <input type="text" name="name" defaultValue={profile.name} onChange={(e) => handleChange(e)} />
                <input type="text" name="surname" defaultValue={profile.surname} onChange={(e) => handleChange(e)} />
                <input type="text" name="email" defaultValue={profile.email} onChange={(e) => handleChange(e)} />
                <button type="submit">Edit</button>
            </form>
        </div>
    )
}

export default EditProfile
