/* eslint-disable no-unused-expressions */
/* eslint-disable no-sequences */
import React, { useState } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';
import { Link } from 'react-router-dom';


const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();


    const url = "http://localhost:3002/auth"


    const login = () => {
        const data = { email: email, password: password }
        Axios.post(url + "/login", data).then((response) => {
            if (response.data.error) {
                // alert(response.data)
                // if there is any error, don't create an accessToken to localstorage, else if any error doesn't exist, create accessToken
                console.log("response if condition error: ", response.data.error);
            }
            else {
                console.log("token: ", response.data.token);
                localStorage.setItem("token", response.data.token), //this comes from routes -> const accessToken -> res.json({token, username, id})
                    redirect();
            }
        })
    }

    const redirect = () => {
        history.push("/dashboard")
    }

    return (
        <div className="container-bcg">
            <div className="form login-form d-flex flex-column justify-content-center m-auto">
                <h1>Login</h1>
                <div className="user-input d-flex flex-column">
                    <label>Email</label>
                    <input type="text" onChange={(event) => { setEmail(event.target.value) }} />
                </div>
                <div className="user-input d-flex flex-column">
                    <label>Password</label>
                    <input type="password" onChange={(event) => { setPassword(event.target.value) }} />
                </div>

                <div className="button-span d-flex justify-content-between align-items-center">
                    <button onClick={login}>Login</button>
                    <span>Create an account <Link to="create-account">here</Link></span>
                </div>
            </div>
        </div>
    )
}

export default Login
