    }

    useEffect(() => {
        getNotes();
    }, [])

    return (
        <div className="my-notes">
            <h1>My notes</h1>
            {
                notes.map((note) => {
                    return <div key={note.id}>
                        <h3>{note.title}</h3>
                        <div>{note.description}</div>
                        <div>{note.createdAt}</div>
                        <div>{date}</div>
                        <Edit editNote={editNote} />
                        <Delete />
                    </div>
                })
            }
        </div>
    )
}

export default MyNotes
