import React, { useState, useEffect } from 'react'
// import { CgProfile } from "react-icons/cg";
import Axios from 'axios'

const Profile = () => {
    // eslint-disable-next-line no-unused-vars
    const [user, setUser] = useState("");

    const url = "http://localhost:3002/auth";
    const token = localStorage.getItem("token");
    const userName = localStorage.getItem("user");


    const getUser = () => {
        Axios.get((url), {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            console.log("Profile profile", response)
            setUser(response.data.name)
        })
    }

    const logOut = () => {
        // localStorage.removeItem("token");
        localStorage.clear();
        window.location.href = '/login'
    }

    useEffect(() => {
        getUser();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    return (
        <>
            {/* <CgProfile />
            <h3>{userName}</h3>
            <button onClick={logOut}>Log out</button> */}
            {/* 
            <div className="half flex-grow-1">
                <label htmlFor="profile2" className="profile-dropdown">
                    <input type="checkbox" id="profile2" />
                    <img src="https://cdn0.iconfinder.com/data/icons/avatars-3/512/avatar_hipster_guy-512.png" alt="profile" />
                    <span>{userName}</span>
                    <label htmlFor="profile2"><i className="mdi mdi-menu"></i></label>
                    <ul>
                        <li>Edit profile</li>
                        <li>Settings</li>
                        <li>Change password</li>
                        <li className="on-mobile">Notes</li>
                        <li className="on-mobile">Todos</li>
                        <li><button onClick={logOut}>Log out</button></li>
                    </ul>
                </label>
            </div> */}

            <div class="dropup">
                <input type="checkbox" id="profile2" />
                <img src="https://cdn0.iconfinder.com/data/icons/avatars-3/512/avatar_hipster_guy-512.png" alt="profile" />
                <span>{userName}</span>
                <label htmlFor="profile2"><i className="mdi mdi-menu"></i></label>
                <div class="dropup-content">
                    <ul>
                        <li>Edit profile</li>
                        <li>Settings</li>
                        <li>Change password</li>
                        <li className="on-mobile">Notes</li>
                        <li className="on-mobile">Todos</li>
                        <li><button onClick={logOut}>Log out</button></li>
                    </ul>
                </div>
            </div>
        </>
    )
}

export default Profile
