import React, { useState } from 'react'
import Axios from 'axios'
import { toast } from 'react-toastify'

const ChangePassword = () => {

    const [oldPassword, setOldPaasword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const token = localStorage.getItem("token");
    const url = "http://localhost:3002/change-password"

    const changePassword = (e) => {
        e.preventDefault();

        Axios.put(url, { oldPassword, newPassword, confirmPassword }, {
            headers: {
                "token": `${token}`
            }
        })
            .then((response) => {
                if (!(oldPassword && newPassword && email && password && confirmPassword)) {
                    if (oldPassword) {
                        toast.warn(response.data.error)
                    }
                    else if (newPassword !== confirmPassword) {
                        toast.warn(response.data.error)
                    }
                    else {
                        toast.success(response.data)
                    }
                }

            })
            .catch((error) => {
                toast.warn(error)
                console.log(error.data.error)
            })
    }

    return (
        <div className="container-bcg d-flex">
            <form onSubmit={(e) => changePassword(e)} className="form login-form d-flex flex-column justify-content-center m-auto" autoComplete="off">
                <h2 className="dark-blue">Change password</h2>
                <div className="user-input d-flex flex-column">
                    <input type="text" name="oldPassword" placeholder="Old password" onChange={(event) => { setOldPaasword(event.target.value) }} />
                </div>
                <div className="user-input d-flex flex-column">
                    <input type="text" name="newPassword" placeholder="New password" onChange={(event) => { setNewPassword(event.target.value) }} />
                </div>
                <div className="user-input d-flex flex-column">
                    <input type="text" name="confirmPassword" placeholder="Confirm new password" onChange={(event) => { setConfirmPassword(event.target.value) }} />
                </div>
                <div className="button-span d-flex justify-content-center align-items-center">
                    <button type="submit">Edit</button>
                </div>
            </form>
        </div>
    )
}

export default ChangePassword
