import React from 'react'
import { Link } from 'react-router-dom'
import { useState } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';
import { BsFillCalendar3WeekFill } from "react-icons/bs";
            toast.success(response.data.message)

const SignUp = () => {
    const [name, setName] = useState("");
    const [surname, setSurname] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const url = "http://localhost:3002/auth";
    let history = useHistory()
    const signUp = () => {

        const data = { name, surname, email, password, confirmPassword }

        Axios.post(url + "/signup", data)
            .then((response) => {
                if (response.data.error) {
                    console.log(response.data)
                }
                else {
                    console.log(response.data, "user has been successfully created")
                    // console.log(response)
                    history.push("/login")
                }

            })

    }

    return (
        <div className="container-bcg">
            <div className="form d-flex flex-column justify-content-center">
                <div className="icon text-center">
                    <BsFillCalendar3WeekFill />
                </div>
                <h2 className="dark-blue">Sign up</h2>
                <div className="form-space">
                    {/* <label>Name</label> */}
                    <input type="text" placeholder="Name" onChange={(event) => { setName(event.target.value) }} />
                    {/* <label>Surname</label> */}
                    <input type="text" placeholder="Surname" onChange={(event) => { setSurname(event.target.value) }} />
                    {/* <label>Email</label> */}
                    <input type="text" placeholder="Email" onChange={(event) => { setEmail(event.target.value) }} />
                    {/* <label>Password</label> */}
                    <input type="password" placeholder="Password" onChange={(event) => { setPassword(event.target.value) }} />
                    {/* <label>Confirm Password</label> */}
                    <input type="password" placeholder="Confirm password" onChange={(event) => { setConfirmPassword(event.target.value) }} />

                </div>
                <div className="button-span text-center">
                    <div>
                        <button onClick={signUp}>Sign up</button>
                    </div>
                    <span>Already have an account? <Link to="/login">Log in</Link></span>
                </div>
            </div>
        </div>
    )
}

export default SignUp
