/* eslint-disable no-unused-vars */
import React, { useEffect, useState } from 'react';
import Axios from 'axios'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'
import MyNotes from './notes/MyNotes'
import AddNote from './notes/AddNote'
import { useHistory, useRouteMatch } from 'react-router';
import Home from './Home';
import Navbar from './Navbar';

const Dashboard = () => {

    const [user, setUser] = useState("");
    const userLoggedIn = localStorage.getItem("user")
    const [notes, setNotes] = useState([])
    const [date, setDate] = useState();
    const url = "http://localhost:3002";
    const token = localStorage.getItem("token");

    let history = useHistory()

    const getUser = () => {
        Axios.get((url + "/auth"), {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            // console.log("response", response)
            setUser(response)
        })
    }

    // const getNotes = () => {

    // }

    const deleteNote = (id) => {
        Axios.delete((url + `/notes/${id}`), {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            setNotes(notes.filter((note) => {
                console.log("note from delete method " + note.id)
                return note.id !== id
            }))
        })
    }

    useEffect(() => {
        getUser()
        
    }, [])


    const addNote = (title, description) => {
        // console.log(e)
        const data = { title: title, description: description }

        Axios.post(url + "/notes/add-note", data, {
            headers: {
                "token": localStorage.getItem("token")
            }
        }).then((response) => {
            if (response.data.error) {
                console.log("add note error ", response.data.error)
            }
            else {
                console.log("note added successfully ", response.data);
                setNotes([...notes, { title: response.data.title, description: response.data.description }])
                history.push("/dashboard/my-notes")
            }
        })
    }


    let match = useRouteMatch();

    return (
        <div className="full-web-page d-flex">
            <Router>
                <div className="left-sidebar">
                    <ul>
                        <li>
                            <Link to={`${match.url}/add-note`}>Add note</Link>
                        </li>
                        <li>
                            <Link to={`${match.url}/my-notes`}>My notes</Link>
                        </li>
                    </ul>
                </div>
                <div className="dashboard d-flex flex-grow-1">
                    {/* <button onClick={addNote}>Add note</button>
                    <MyNotes />
                    <button onClick={allNotes}>all notes</button> */}

                    <Switch>
                        <Route exact path={`${match.path}`}>
                            <Home />
                        </Route>
                        <Route exact path={`${match.path}/add-note`}>
                            <AddNote addNote={addNote} notes={notes} />
                        </Route>
                        <Route exact path={`${match.path}/my-notes`}>
                            <MyNotes notes={notes} deleteNote={deleteNote} />
                        </Route>
                        <Route exact path="**" component={() => "404 NOT FOUND"} />
                    </Switch>
                </div>
                <div className="right-sidebar">
                    <Navbar notes={notes} />
                </div>
            </Router>
        </div>
    )
}

export default Dashboard
