import './App.css';
import { useEffect, useState } from 'react'
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import Home from './components/Home'
import Login from './components/Login'
import SignUp from './components/SignUp';
import { AuthContext } from "./helpers/AuthContext";
import ProtectedRoutes from './protectedRoutes/ProtectedRoutes';
import Axios from 'axios'
import { useHistory } from 'react-router';
import Profile from './components/Profile';

function App() {

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [token, setToken] = useState("")

  const history = useHistory()

  const url = "http://localhost:3002"

  useEffect(() => {
    Axios.get(url, {
      headers: {
        accessToken: localStorage.getItem("accessToken")
      }
    })
      .then((response) => {
        if (response.config.headers.accessToken) {
          setToken(localStorage.getItem("accessToken"))
          setIsLoggedIn(true)
          console.log("rrusponsi   " + response.config.headers.accessToken)

        }
        else {
          console.log("error te responsi   ", response.data.error)
          setIsLoggedIn(false)
          console.log("is logged in  ", isLoggedIn)
          history.push("/")
        }
      })
      .catch((err) => {
        console.log("error login.js    ", err)
      })
  }, [])

  // useEffect(() => {
  //   Axios.get(url, {
  //     headers: {
  //       accessToken: localStorage.getItem("accessToken")
  //     }
  //   })
  //     .then((response) => {
  //       if (response.data.error) {
  //         console.log(response.data.error)
  //       }
  //       else {
  //         console.log("data- ----", response.data)
  //       }
  //     })
  // })


  return (
    <div>
      <AuthContext.Provider value={{ token, setToken, isLoggedIn, setIsLoggedIn }}>
        <Router>
          {/* {
            !isLoggedIn ? (
              <>
                <Link to="/login">Login</Link>
                <Link to="/register">Sign up</Link>
              </>) : (<button>Log out</button>)} */}
          <Switch>
            <Route path="/" exact component={Login} />
            <Route path="/register" component={SignUp} />
            <ProtectedRoutes path="/home" exact component={Home} />
            <ProtectedRoutes path="/profile" exact component={Profile} />
          </Switch>
        </Router>
      </AuthContext.Provider>
    </div>
  );
}

export default App;
