    // const [email, setEmail] = useState(user.email)

    const handleChange = (e) => {
        console.log(profile)
    }

    const editProfile = (e) => {
        e.preventDefault();
        console.log("edited")
    }

    return (
        <div>
            <h1>Bleona</h1>
            <form onSubmit={(e) => editProfile(e)}>
                <input type="text" name="name" defaultValue={user.name} onChange={(e) => handleChange(e)} />
                <input type="text" name="surname" defaultValue={user.surname} onChange={(e) => handleChange(e)} />
                <input type="text" name="email" defaultValue={user.email} onChange={(e) => handleChange(e)} />
                <button>Edit</button>
            </form>
        </div>
    )
}

export default EditProfile
