import React from 'react'
import { useState } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';

const SignUp = () => {
    const [name, setName] = useState("");
    const [surname, setSurname] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const url = "http://localhost:3002/auth";
    let history = useHistory()
    const signUp = () => {

        const data = { name, surname, email, password, confirmPassword }

        Axios.post(url + "/signup", data)
            .then((response) => {
                if (response.data.error) {
                    console.log(response.data.error)
                }
                else {
                    console.log(response.data, "user has been successfully ")
                    // console.log(response)
                    history.push("/login")
                }

            })

    }

    return (
        <div className="signUpForm">
            <h1>Sign up</h1>
            <div className="user-name">
                <div>
                    <label>Name</label>
                    <input type="text" onChange={(event) => { setName(event.target.value) }} />
                </div>
                <div>
                    <label>Surnam</label>
                    <input type="text" onChange={(event) => { setSurname(event.target.value) }} />
                </div>
            </div>
            <div className="user-email">
                <label>Email</label>
                <input type="text" onChange={(event) => { setEmail(event.target.value) }} />
            </div>
            <div className="user-name">
                <div>
                    <label>Password</label>
                    <input type="password" onChange={(event) => { setPassword(event.target.value) }} />
                </div>
                <div>
                    <label>Confirm Password</label>
                    <input type="password" onChange={(event) => { setConfirmPassword(event.target.value) }} />
                </div>
            </div>

            <div>
                <button onClick={signUp}>Sign up</button>

            </div>
        </div>
    )
}

export default SignUp
