import Axios from 'axios';
import React, { useState } from 'react'

const Popup = ({ notes, handleClose, content }) => {

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const url = "http://localhost:3002/notes"
    const token = localStorage.getItem("token");

    const [note, setNote] = useState([])

    const handleChange = (e) => {
        setNote({
            ...note,
            [e.target.name]: e.target.value,
        });
    };

    const data = { title, description }

    const handleSubmit = (e, id) => {
        e.preventDefault()
        Axios.put((url + `/${id}`), data, {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            console.log(response.data)
        })
            .catch((err) => {
                console.log(err)
            })

    }

    // console.log("popup", notes)
    return (
        <div className="popup-box">
            <div className="box">
                <span className="close-icon" onClick={handleClose}>X</span>
                {/* {content} */}
                <h2>{notes.title}</h2>
                {/* <input type="text" name="title" value={notes.title} onChange={(e) => handleChange(e)} /> */}
                {/* <input type="text" name="description" value={notes.description} onChange={(e) => handleChange(e)} /> */}
                {/* <button onClick={() => updateData(notes.id)}>Save data</button> */}

                <form onSubmit={(e) => handleSubmit(e, notes.id)}>
                    <input type="text" name="title" value={notes.title} onChange={(e) => handleChange(e)} />
                    <input type="text" name="description" value={notes.description} onChange={(e) => handleChange(e)} />
                    <input
                        type="submit"
                        className="btn btn-primary mt-2"
                        value="Submit"
                    />
                </form>
            </div>
        </div>
    )
}

export default Popup
