import React, { useEffect, useState } from 'react'
import Axios from 'axios'
import Edit from '../../Buttons/Edit'
import Delete from '../../Buttons/Delete'

const MyNotes = () => {
    const [notes, setNotes] = useState([])
    const [date, setDate] = useState();
    const [user, setUser] = useState("");

    const url = "http://localhost:3002/notes";
    const userUrl = "http://localhost:3002/auth";
    const token = localStorage.getItem("token");

    // const getUser = () => {
    //     Axios.get((userUrl), {
    //         headers: {
    //             "token": `${token}`
    //         }
    //     }).then((response) => {
    //         // console.log("navbar profile", response)
    //         setUser(response.data.name)

    //     })
    // }

    const getNotes = () => {
        Axios.get((url), {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            console.log("notes ", response.data)
            setNotes(response.data)
            // const date = Date.parse(response.data.createdAt)

            let allNotes = response.data;
            for (let i = 0; i < allNotes.length; i++) {
                // console.log(allNotes[i].createdAt)
                let dateFormat = allNotes[i].createdAt;

                let date = new Date(dateFormat);
                let year = date.getFullYear();
                let month = date.getMonth() + 1;
                let dt = date.getDate();

                if (dt < 10) {
                    dt = '0' + dt;
                }
                if (month < 10) {
                    month = '0' + month;
                }


                let formattedDate = dt + "." + month + "." + year;
                setDate(formattedDate);
            }

            console.log("user from notes ", user);
            console.log("notes from notes ", response.data)
        })
    }





    const editNote = (id) => {
        console.log(id)
    }

    const deleteNote = (id) => {
        Axios.delete((url + `/${id}`), {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            setNotes(notes.filter((note) => {
                console.log("note from delete method " + note.id);
                return note.id !== id
            }))
        })
    }

    useEffect(() => {
        getUser();
        getNotes();
    }, [])

    return (
        <div className="my-notes">
            <h1>My notes</h1>
            {
                notes.map((note) => {
                    return <div key={note.id}>
                        <h3>{note.title}</h3>
                        <div>{note.description}</div>
                        <div>{note.createdAt}</div>
                        <div>{date}</div>
                        <Edit editNote={() => editNote(note.id)} />
                        <Delete deleteNote={() => deleteNote(note.id)} />
                    </div>
                })
            }
        </div>
    )
}

export default MyNotes
