import React from 'react'
import { Link } from 'react-router-dom'
import { useState } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';

const SignUp = () => {
    const [name, setName] = useState("");
    const [surname, setSurname] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const url = "http://localhost:3002/auth";
    let history = useHistory()
    const signUp = () => {

        const data = { name, surname, email, password, confirmPassword }

        Axios.post(url + "/signup", data)
            .then((response) => {
                if (response.data.error) {
                    console.log(response.data)
                }
                else {
                    console.log(response.data, "user has been successfully created")
                    // console.log(response)
                    history.push("/login")
                }

            })

    }

    return (
        <div className="form d-flex flex-column justify-content-center m-auto">
            <h1>Sign up</h1>
            <div className="user-name user-input d-flex justify-content-between">
                <div>
                    <label>Name</label>
                    <input type="text" placeholder="ex. john" onChange={(event) => { setName(event.target.value) }} />
                </div>
                <div>
                    <label>Surname</label>
                    <input type="text" placeholder="ex. doe" onChange={(event) => { setSurname(event.target.value) }} />
                </div>
            </div>
            <div className="user-input d-flex flex-column">
                <label>Email</label>
                <input type="text" placeholder="ex. john.doe@gmail.com" onChange={(event) => { setEmail(event.target.value) }} />
            </div>
            <div className="user-name user-input d-flex justify-content-between">
                <div>
                    <label>Password</label>
                    <input type="password" placeholder="password" onChange={(event) => { setPassword(event.target.value) }} />
                </div>
                <div>
                    <label>Confirm Password</label>
                    <input type="password" placeholder="confirm password" onChange={(event) => { setConfirmPassword(event.target.value) }} />
                </div>
            </div>

            <div className="btn-login-sign d-flex justify-content-between align-items-center">
                <button onClick={signUp}>Sign up</button>
                <span>Already have an account? <Link to="/login">Log in</Link></span>
            </div>
        </div>
    )
}

export default SignUp
