/* eslint-disable no-unused-vars */
import React, { useEffect, useState } from 'react';
import Axios from 'axios'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'
import MyNotes from './notes/MyNotes'
import Notes from './notes/Notes'
import AddNote from './notes/AddNote'
import { useHistory } from 'react-router';
import Navbar from './Navbar';

const Dashboard = ({ match }) => {

    const [user, setUser] = useState("");
    // const [isLoggedIn, setIsLoggedIn] = useState(false)

    const url = "http://localhost:3002";
    const token = localStorage.getItem("token");
    let history = useHistory();

    const getUser = () => {
        Axios.get((url + "/auth"), {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            // console.log("response", response)
            setUser(response)
        })
    }


    useEffect(() => {
        getUser();
    }, [])

    const addNote = () => {
        history.push("/add-note")
    }
    const allNotes = () => {
        history.push("/notes")
    }

    return (
        <>
            <header className="header">
                <Navbar getUser={getUser} />
            </header>
            <div className="container-page">
                <div>
                    <ul>
                        <li>
                            <Link to={`${match.url}/add-note`}>Add Note</Link>
                            <Link to={`${match.url}/my-notes`}>My notes</Link>
                        </li>
                    </ul>
                </div>

                <Route path={`${match.path}/:component`} render={({ match }) => (<div><h3> {match.params.component} </h3></div>)} />
            </div>
        </>
    )
}

export default Dashboard
