/* eslint-disable no-unused-expressions */
/* eslint-disable no-sequences */
import React, { useState } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';


const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();


    const url = "http://localhost:3002/auth"


    const login = () => {
        const data = { email: username, password: password }
        Axios.post(url + "/login", data).then((response) => {
            if (response.data.error) {
                // alert(response.data)
                // if there is any error, don't create an accessToken to localstorage, else if any error doesn't exist, create accessToken
                console.log("response if condition error: ", response.data.error);
            }
            else {
                console.log("token: ", response.data.token);
                localStorage.setItem("token", response.data.token), //this comes from routes -> const accessToken -> res.json({token, username, id})
                    redirect();
            }
        })
    }

    const redirect = () => {
        history.push("/dashboard")
    }

    return (
        <div className="signUpForm d-flex flex-column justify-content-center m-auto">
            <h1>Sign up</h1>
            <div className="user-name user-input d-flex justify-content-between">
                <div>
                    <label>Name</label>
                    <input type="text" onChange={(event) => { setName(event.target.value) }} />
                </div>
                <div>
                    <label>Surnam</label>
                    <input type="text" onChange={(event) => { setSurname(event.target.value) }} />
                </div>
            </div>
            <div className="user-input d-flex flex-column">
                <label>Email</label>
                <input type="text" onChange={(event) => { setEmail(event.target.value) }} />
            </div>
            <div className="user-name user-input d-flex justify-content-between">
                <div>
                    <label>Password</label>
                    <input type="password" onChange={(event) => { setPassword(event.target.value) }} />
                </div>
                <div>
                    <label>Confirm Password</label>
                    <input type="password" onChange={(event) => { setConfirmPassword(event.target.value) }} />
                </div>
            </div>

            <div>
                <button onClick={signUp}>Sign up</button>

            </div>
        </div>
    )
}

export default Login
