import { useState } from "react"

const Modal = () => {

    const [show, setShow] = useState(false);

    const toggle = () => setShow(!show)

    return (
        <div className='open-modal'>
            <button className={show ? 'hide-button' : 'show-modal-button'} onClick={toggle}>Show modal</button>

            <div>
                <div id="outer-container" className={show ? 'modal modal-content-active' : 'close'}>
                    {
                        show ?
                            <div className='modal-box'>
                                <button onClick={toggle}>X</button>
                                <div>
                                    <h1>Modal Content</h1>
                                </div>
                            </div>
                            :
                            null
                    }
                </div>
            </div>
        </div>
    )
}

export default Modal
