import React from 'react';
import { useEffect, useState, useContext } from 'react'
import Axios from 'axios'
import { useHistory } from 'react-router';
import { AuthContext } from '../helpers/AuthContext'


const Home = () => {
    const { isLoggedIn, setIsLoggedIn, setToken, token } = useContext(AuthContext);

    let history = useHistory()

    const url = "http://localhost:3002";
    const ls_token = localStorage.getItem("token")
    console.log(ls_token)

    useEffect(() => {
        Axios.get(url)
            .then((response) => {
                if (response.config.headers.token) {
                    setToken(localStorage.setItem("token", response.config.headers.token))
                }
                else {
                    console.log("error te responsi   ", response.data.error)
                    setIsLoggedIn(false)
                    console.log("is logged in  ", isLoggedIn);
                    history.push("/")
                }
            })
            .catch((err) => {
                console.log("error login.js    ", err)
            })
    }, [])

    return (
        <div>
            <h1>Welcome to home</h1>
        </div>
    )
}

export default Home
