/* eslint-disable no-unused-expressions */
/* eslint-disable no-sequences */
import React, { useState } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';


const Login = () => {
    const [name, setName] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();


    const [user, setUser] = useState("");

    const url = "http://localhost:3002/auth";
    const token = localStorage.getItem("token");

    const getUser = () => {
        Axios.get((url), {
            // headers: {
            //     "token": `${token}`
            // }
        }).then((response) => {
            console.log("response", response)
            setUser(response.data.name)
        })
    }

    useEffect(() => {
        getUser();
    }, [])

    // const url = "http://localhost:3002/auth"


    const login = () => {
        const data = { name: name, password: password }
        Axios.post(url + "/login", data).then((response) => {
            if (response.data.error) {
                // alert(response.data)
                // if there is any error, don't create an accessToken to localstorage, else if any error doesn't exist, create accessToken
                console.log("response if condition error: ", response.data.error);
            }
            else {
                console.log("token: ", response.data.token);
                localStorage.setItem("token", response.data.token), //this comes from routes -> const accessToken -> res.json({token, username, id})
                    redirect();
            }
        })
    }

    const redirect = () => {
        history.push("/dashboard")
    }

    return (
        <div className="loginContainer">
            <h1>Login</h1>
            <label>Username</label>
            <input type="text" onChange={(event) => { setName(event.target.value) }} />

            <label>Password</label>
            <input type="password" onChange={(event) => { setPassword(event.target.value) }} />

            <button onClick={login}>Login</button>
        </div>
    )
}

export default Login
