import React, { useState } from 'react'
import Edit from '../../Buttons/Edit'
// import Delete from '../../Buttons/Delete'
import Axios from 'axios';
import Popup from '../../../helpers/Popup';

const Note = ({ element }) => {

    const [isOpen, setIsOpen] = useState(false);
    const [note, setNote] = useState()
    const url = "http://localhost:3002/notes"

    // const openModal = (id) => {
    //     console.log(id);
    //     setIsOpen(!isOpen);
    //     Axios.get((url + `/${id}`), {
    //         headers: {
    //             "token": localStorage.getItem("token")
    //         }
    //     }).then((response) => {
    //         console.log("this response ", response)
    //         setNote(response.data)
    //     })
    // }
    // console.log("NOTE2NOTE ", note)

    const openModal = (id) => {
        // console.log(id);
        setIsOpen(!isOpen);
        Axios.get((url + `/${id}`), {
            headers: {
                "token": localStorage.getItem("token")
            }
        }).then((response) => {
            // console.log("this response ", response)
            // setNote(response.data)
            console.log(response.data)
        })
    }
    console.log(isOpen)
    // console.log("NOTE2NOTE ", element)

    return (
        <div className="note" key={element.id}>
            {/* <span>{date}</span> */}
            <h4>{element.title}</h4>
            <p>{element.description}</p>
            {/* <div>{note.createdAt}</div> */}
            <div className="dashboard-buttons">
                <Edit editNote={() => openModal(element.id)} />
                {/* <Delete deleteNote={() => deleteNote(note.id)} /> */}
            </div>
            {
                isOpen && <Popup handleClose={openModal} notes={element} />
            }
        </div>
    )
}

export default Note
