import React, { useContext, useState } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';
import { AuthContext } from '../helpers/AuthContext'
import { useAuth } from "./helpers/useAuth";

const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();

    const { setUser } = useContext(AuthContext);
    const { loginUser, error } = useAuth();

    const url = "http://localhost:3002/login"


    const setUserContext = async () => {
        return await A.get('/user').then(res => {
            setUser(res.data.currentUser);
            history.push('/home');
        }).catch((err) => {
            setError(err.response.data);
        })
    }

    const login = () => {
        const data = { username: username, password: password }
        Axios.post(url, data).then((response) => {
            if (response.data.error) {
                // alert(response.data)
                // if there is any error, don't create an accessToken to localstorage, else if any error doesn't exist, create accessToken
                console.log("response if condition error: ", response.data.error);
                setUser(response.data.error)
            }
            else {
                console.log("token: ", response.data);
                localStorage.setItem("accessToken", response.data.token)  //this comes from routes -> const accessToken -> res.json({token, username, id})
                setUser(response.data.username);
                redirect();
            }
        })
    }

    const redirect = () => {
        history.push("/home")
    }

    return (
        <div className="loginContainer">
            <h1>Login</h1>
            <label>Username</label>
            <input type="text" onChange={(event) => { setUsername(event.target.value) }} />

            <label>Password</label>
            <input type="password" onChange={(event) => { setPassword(event.target.value) }} />

            <button onClick={login}>Login</button>
        </div>
    )
}

export default Login
