    const [newPassword, setNewPassword] = useState("");

    const savePassword = () => {
        e.preventDefault();

        Axios.post(url, { email })
            .then((response) => {
                if (response.data.error) {
                    toast.warn(response.data.err)
                }
                else {
                    history.push("/reset-password")
                    toast.success(response.data);
                }
            })
            .catch((err) => {
                console.log(err)
            })

    }

    return (
        <div className="container-bcg">
            <form onSubmit={savePassword} className="form login-form d-flex flex-column justify-content-center">

                <h2 className="dark-blue">Reset password</h2>
                <div className="form-space">
                    <input type="text" placeholder="Email" onChange={(event) => { setToken(event.target.value) }} />
                    <input type="password" placeholder="Password" onChange={(event) => { setNewPassword(event.target.value) }} />

                </div>
                <div className="button-span text-center">
                    <div>
                        <button type="submit">Save new password</button>
                    </div>

                </div>
            </form>
        </div>
    )
}

export default ResetPassword
