import React, { useEffect, useState } from 'react'
import Note from './Note';
import axios from "axios"

const MyNotes = ({ notes, deleteNote, date }) => {
    const [updatedNotes, setUpdatedNotes] = useState([notes])

    useEffect(() => {
        setUpdatedNotes(notes)

        console.log("props changed")
    }, [notes])
    return (
        <div className="switched-components">
            <div className="component-name">
                <div>{notes.length}</div><span className="dark-blue">Your notes</span>
            </div>
            <hr />

            <div className="dashboard-container">
                {/* {
                    notes.map((note) => {
                        return <Note note={note} key={note.id} deleteNote={() => deleteNote(note.id)} />
                    })
                } */}
                {updatedNotes.map((note) => { return <Note note={note} key={note.id} deleteNote={() => deleteNote(note.id)} /> })}
            </div>
        </div >
    )
}

export default MyNotes
