import React, { useState } from 'react'
import Axios from 'axios';

const Popup = ({ notes, handleClose, content }) => {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const url = "http://localhost:3002/notes"

    const editNote = (id) => {
        // console.log(e)
        const data = { title: title, description: description }

        Axios.put(url + `/${id}`, data, {
            headers: {
                "token": localStorage.getItem("token")
            }
        }).then((response) => {
            setNotes([...notes, {
                id: response.data.id,
                title: response.data.title,
                description: response.data.description,
                user: response.data.user,
                createdAt: response.data.createdAt,
                updatedAt: response.data.updatedAt
            }])

            // console.log("popup", notes)
            return (
                <div className="popup-box">
                    <div className="box">
                        <span className="close-icon" onClick={handleClose}>X</span>
                        {/* {content} */}
                        <h2>{notes.title}</h2>
                        <input type="text" value={notes.title} onChange={(e) => setTitle(e.target.value)} />
                        <input type="text" value={notes.description} onChange={(e) => setDescription(e.target.value)} />

                        <button onClick={() => editNote(notes.id)}>Edit note</button>
                    </div>
                </div>
            )
        }
        )
    }
}

export default Popup
