/* eslint-disable no-unused-vars */
import React, { useEffect, useState } from 'react';
import Axios from 'axios'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'
import MyNotes from './notes/MyNotes'
import AddNote from './notes/AddNote'
import { useRouteMatch } from 'react-router';
import Home from './Home';
import Navbar from './Navbar';
import { useHistory } from 'react-router';

const Dashboard = () => {

    const [user, setUser] = useState("");
    const userLoggedIn = localStorage.getItem("user")
    const [notes, setNotes] = useState([])
    const [date, setDate] = useState();
    const url = "http://localhost:3002";
    const token = localStorage.getItem("token");
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    let history = useHistory()

    const getUser = () => {
        Axios.get((url + "/auth"), {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            // console.log("response", response)
            setUser(response)
        })
    }


    const getNotes = () => {
        Axios.get((url + "/notes"), {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            console.log("allNotes", response.data)
            let allNotes = response.data;
            // setNotes(allNotes);
            let filteredNotes = allNotes.filter((eachNote) => {
                return eachNote.user === userLoggedIn
            })
            setNotes(filteredNotes)
            console.log("filteredNotes", filteredNotes)

            for (let i = 0; i < allNotes.length; i++) {
                let dateFormat = allNotes[i].createdAt;

                let date = new Date(dateFormat);
                let year = date.getFullYear();
                let month = date.getMonth() + 1;
                let dt = date.getDate();

                if (dt < 10) {
                    dt = '0' + dt;
                }
                if (month < 10) {
                    month = '0' + month;
                }

                let formattedDate = dt + "." + month + "." + year;
                setDate(formattedDate);
            }
        })
    }


    const addNote = (e) => {
        // console.log(e)
        e.preventDefault();
        const data = { title: title, description: description }

        Axios.post(url + "/add-note", data, {
            headers: {
                "token": localStorage.getItem("token")
            }
        }).then((response) => {
            if (response.data.error) {
                console.log("add note error ", response.data.error)
            }
            else {
                console.log("note added successfully ", response.data);
                // console.log("propsnotesssss, ", propsNotes)
                history.push("/dashboard/my-notes")
            }
        })
    }


    const deleteNote = (id) => {
        Axios.delete((url + `/notes/${id}`), {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            setNotes(notes.filter((note) => {
                console.log("note from delete method " + note.id)
                return note.id !== id
            }))
        })
    }


    useEffect(() => {
        getNotes();
        getUser();

        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])




    let match = useRouteMatch();

    return (
        <div className="full-web-page d-flex">
            <Router>
                <div className="left-sidebar">
                    <ul>
                        <li>
                            <Link to={`${match.url}/add-note`}>Add note</Link>
                        </li>
                        <li>
                            <Link to={`${match.url}/my-notes`}>My notes</Link>
                        </li>
                    </ul>
                </div>
                <div className="dashboard d-flex flex-grow-1">
                    {/* <button onClick={addNote}>Add note</button>
                    <MyNotes />
                    <button onClick={allNotes}>all notes</button> */}

                    <Switch>
                        <Route exact path={`${match.path}`}>
                            <Home />
                        </Route>
                        <Route exact path={`${match.path}/add-note`}>
                            <div className="container-bcg d-flex">
                                <form onSubmit={addNote} className="form login-form d-flex flex-column justify-content-center m-auto">
                                    <h1>Add note</h1>
                                    <div className="user-input d-flex flex-column">
                                        <label>Title</label>
                                        <input type="text" onChange={(event) => { setTitle(event.target.value) }} />
                                    </div>
                                    <div className="user-input d-flex flex-column">
                                        <label>Description</label>
                                        <input type="text" onChange={(event) => { setD(event.target.value) }} />
                                    </div>

                                    <div className="button-span d-flex justify-content-between align-items-center">
                                        <button type="submit">Save note</button>
                                        {/* <span>Create an account <Link to="create-account">here</Link></span> */}
                                    </div>
                                </form>
                            </div>/>
                        </Route>
                        <Route exact path={`${match.path}/my-notes`}>
                            <MyNotes notes={notes} deleteNote={deleteNote} />
                        </Route>
                        <Route exact path="**" component={() => "404 NOT FOUND"} />
                    </Switch>
                </div>
                <div className="right-sidebar">
                    <Navbar />
                </div>
            </Router>
        </div>
    )
}

export default Dashboard
