import Axios from 'axios';
import React, { useState } from 'react'

const Popup = ({ notes, handleClose, content }) => {

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const url = "http://localhost:3002/notes"
    const token = localStorage.getItem("token");

    const data = { title, description }
    const updateData = (id) => {
        Axios.put((url + `/${id}`), data, {
            headers: {
                "token": `${token}`
            }
        }).then((response) => {
            console.log(response.data)
        })
            .catch((err) => {
                console.log(err)
            })

    }

    // console.log("popup", notes)
    return (
        <div className="popup-box">
            <div className="box">
                <span className="close-icon" onClick={handleClose}>X</span>
                {/* {content} */}
                <h2>{notes.title}</h2>
                <input type="text" value={notes.title} onChange={(e) => setTitle(e.target.value)} />
                <input type="text" defaultValue={notes.description} onChange={(e) => setDescription(e.target.value)} />
                <button onClick={() => updateData(notes.id)}>Save data</button>
            </div>
        </div>
    )
}

export default Popup
