import './App.css';
import { useEffect, useState, useContext } from 'react'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'
import Home from './components/Home'
import Login from './components/Login'
import SignUp from './components/SignUp';
import { AuthContext } from "./helpers/AuthContext";
import ProtectedRoutes from './protectedRoutes/ProtectedRoutes';
import Axios from 'axios'

function App() {

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [token, setToken] = useState("")

  // const { token, setToken, isLoggedIn, setIsLoggedIn } = useContext(AuthContext);

  const url = "http://localhost:3002"

  useEffect(() => {
    Axios.get(url, {
      headers: {
        accessToken: localStorage.getItem("accessToken")
      }
    })
      .then((response) => {
        if (response.data.error) {
          console.log("error te responsi   ", response.data.error)
          setIsLoggedIn(isLoggedIn)
          console.log("isLoggedIn   ", isLoggedIn)
          // console.log("is logged in  ", isLoggedIn)
        }
        else {
          console.log("data- ----", response.config.headers.accessToken)
          console.log("responseeeee ", response)
          setToken(response.config.headers.accessToken)
          setIsLoggedIn(!isLoggedIn)
          console.log("isLoggedIn   ", isLoggedIn)
          // console.log("else is logged in ", isLoggedIn)

        }
      })
      .catch((err) => {
        console.log("error login.js    ", err)
      })
  }, [])

  // useEffect(() => {
  //   Axios.get(url, {
  //     headers: {
  //       accessToken: localStorage.getItem("accessToken")
  //     }
  //   })
  //     .then((response) => {
  //       if (response.data.error) {
  //         console.log(response.data.error)
  //       }
  //       else {
  //         console.log("data- ----", response.data)
  //       }
  //     })
  // })


  return (
    <div>
      <AuthContext.Provider value={{ token, setToken, isLoggedIn, setIsLoggedIn }}>
        <Router>
          {/* {
            !isLoggedIn ? (
              <>
                <Link to="/login">Login</Link>
                <Link to="/register">Sign up</Link>
              </>) : (<button>Log out</button>)} */}
          <Switch>
            <Route path="/" exact component={Login} />
            <ProtectedRoutes path="/home" exact component={Home} />
            <Route path="/register" component={SignUp} />
          </Switch>
        </Router>
      </AuthContext.Provider>
    </div>
  );
}

export default App;
