/* eslint-disable no-unused-vars */
import React, { useEffect, useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom'
import Login from './components/Login'
import SignUp from './components/SignUp';
import Dashboard from './components/dashboard/Dashboard'
import Profile from './components/dashboard/Profile';
import AddNote from './components/dashboard/notes/AddNote';

import ProtectedRoutes from './protectedRoutes/ProtectedRoutes';
import MyNotes from './components/dashboard/notes/MyNotes';
import Notes from './components/dashboard/notes/Notes';

// import { useEffect, useState } from 'react';
// import LogOut from './components/dashboard/LogOut';
// import Axios from 'axios'




function App() {
  const token = localStorage.getItem("token");
  const [isLoggedIn, setIsLoggedIn] = useState(false)


  // const [isLoggedIn, setIsLoggedIn] = useState(false)
  // const token = localStorage.getItem("token");

  // useEffect(() => {
  //   if (token) {
  //     setIsLoggedIn(true);
  //   }
  //   else {
  //     setIsLoggedIn(false)
  //   }
  // }, [])

  useState(() => {

    if (token) {
      setIsLoggedIn(true)
    }
    console.log(isLoggedIn)
  })



  return (
    <div>
      <Router>
        {/* {
          !isLoggedIn ? (
            <>
              <Link to="/login">Login</Link>
              <Link to="/create-account">Sign up</Link>
            </>) : (<LogOut />)} */}
        <Switch>
          <Route path="/login" exact component={Login} />
          <Route path="/create-account" component={SignUp} />
          <ProtectedRoutes path="/dashboard" exact component={Dashboard} />
          <ProtectedRoutes path="/profile" exact component={Profile} />
          <ProtectedRoutes path="/add-note" exact component={AddNote} />
          <ProtectedRoutes path="/my-notes" exact component={MyNotes} />
          <ProtectedRoutes path="/notes" exact component={Notes} />
          <Route exact path="/">
            <Redirect exact from="/" to="login" />
          </Route>
          <Route path="**" component={() => "404 NOT FOUND"} />
        </Switch>
      </Router>
    </div>
  );
}

export default App;
